package ficha10;

public class Ex1D {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		int tamanho = 4;
		 for(int l = 1; l <= tamanho; l++) {
			 for ( int c = 1; c <= tamanho; c++) {
				if(c == l) {
					System.out.print("1 ");
				} else {
					System.out.print("0 ");
				}
				}
			 System.out.print("\n");
			 
		 }
	}
		 }
	

